def hamming(str1, str2):
    distance = 0
    if len(str1) != len(str2):
        return "Error, different length input"
    else:
        for i in range(len(str1)):
            if str1[i] != str2[i]:
                distance += 1
    return distance


if __name__=="__main__":
    print(hamming('', ''))
    